INSERT INTO `articleBase` VALUES ('邓金辉', '2075', '园长', '', '', 'ios工程师', '<p>岗位:ios开发工程师</p><p>个人介绍：公司产品iOS版本的整体构架设计</p><p>1.根据需求进行IOS平台客户端软件的设计、开发和维护</p><p>2.与项目相关人员配合共同完成应用软件的开发设计工作&nbsp;</p><p>3.遵循软件开发流程，进行应用及人机界面软件模块的设计和实现</p><p>4.IOS平台开发环境（平台及工具软件）的设计、实现和维护&nbsp;</p><p>5.按照项工作目计划在保证质量的前提下、按时完成开发任务</p><p>6.负责与服务器端调试，确保应用质量。 &nbsp; &nbsp;&nbsp;</p><p>7.适应性维护\r\n8.提高生产的效率，保障系统的稳定性及可靠性&nbsp;</p><p>9.提供技术指导，促进系统操作技术和译码编程的有效使用.&nbsp;</p><p>10.跟踪IT技术进展，做好技术储备 &nbsp; &nbsp; &nbsp;&nbsp;</p><p>11.协助相关应用软件的安装调试工作</p><p><br/></p>', '0', '0', null, null, '145', null, null, null, null, '0', null, null, null, '911', 'be66b36daf1f4557ac8149aef9b9ac66', '453', null, '0', '0', '453', '2015-12-09 10:57:05', '2015-12-07 13:44:19');
