INSERT INTO `articleBase` VALUES ('谭黎', '2291', '园长', '', '', '', '<p>岗位:网页设计师</p><p>岗位职责：</p><p>1、负责公司网站的页面设计及美观优化;</p><p>2、负责网站各类专题模板的设计及制作;</p><p>3、负责网站各类活动的宣传广告、标语的图片设计;</p><p>4、负责网站产品的美工设计，对交互体验有较为深入的了解;</p><p>5、对页面进行持续的优化，不断提升访问者的用户体验;</p><p>6、编写可复用的用户界面组件;</p><p>7、协同其他技术部门，做前端开发技术支持。</p><p><br/></p>', '0', '0', null, null, '145', null, null, null, null, '0', null, null, null, '1181', '8e76e0f9225a4c2e945b0da413f08988', '453', null, '0', '0', '0', '2015-12-18 13:37:42', '2015-12-18 13:37:42');
INSERT INTO `articleBase` VALUES ('刘娟', '', '园长', '', '', '', '<p>姓名：刘娟</p><p>性别：女</p><p>班级：技术部</p><p>岗位：软件测试工程师</p><p>荣誉：优秀员工</p><p><br/></p><p>事迹:&nbsp;</p><p>按照测试流程和计划，构建测试环境，设计测试脚本和用例，执行测试脚本和测试用例，寻找Bug。分析问题所在并进行准确定位和验证，按照标准格式填写并提交Bug报告；跟踪并验证Bug，并确认问题得以解决；按照标准格式填写并提交测试报告，编写其他相关文档，完成软件开发的集成测试工作。</p><p><br/></p><p><br/></p>', '0', '0', null, null, '146', null, null, null, null, '0', null, null, null, '1182', 'c55de8ea958343ed9ab86b9307d02ed0', '453', null, '0', '0', '453', '2015-12-18 14:18:07', '2015-12-18 13:58:49');
