mapper.select:
SELECT t.*
FROM articleBase t



		ORDER BY t.id desc


		ORDER BY ${order}



	LIMIT
	#{firstResult,jdbcType=INTEGER},#{pageSize,jdbcType=INTEGER}

SELECT DISTINCT(a.id),a.* FROM articleBase a,scStudent s Where (a.schoolId=#{schoolId,jdbcType=INTEGER} and a.articleReceiver=4 or a.articleReceiver=2 and a.classId=#{classId,jdbcType=INTEGER} or a.articleReceiver=1 and a.userId=#{userId,jdbcType=INTEGER}) and a.sysClassId=#{sysClassId,jdbcType=INTEGER} and a.isDeleted = 0
and a.id<#{firstLastId,jdbcType=INTEGER}
order by a.id desc LIMIT 0,#{pageCount,jdbcType=INTEGER};

SELECT DISTINCT(a.id),a.*
FROM articleBase a where
a.sysClassId=#{sysClassId,jdbcType=INTEGER} and a.isDeleted = 0

	 and a.id<#{firstLastId,jdbcType=INTEGER}  

and (a.schoolId=#{schoolId,jdbcType=INTEGER}
and a.articleReceiver=4 or a.userId=#{userId,jdbcType=INTEGER} and
a.articleReceiver=1
or a.articleReceiver=2 and a.classId=0 and
a.schoolId=#{schoolId,jdbcType=INTEGER}
)
order by
a.id desc
LIMIT
0,#{pageCount,jdbcType=INTEGER};

SELECT DISTINCT(a.id),a.* FROM articleBase a Where a.sysClassId=#{sysClassId,jdbcType=INTEGER} and a.isDeleted = 0
and a.id<#{firstLastId,jdbcType=INTEGER}
and (a.schoolId=#{schoolId,jdbcType=INTEGER} and a.articleReceiver=4 or a.userId=#{userId,jdbcType=INTEGER} and a.articleReceiver=1 or a.userId=#{userId,jdbcType=INTEGER} and a.classId=0 and a.articleReceiver=2 ) order by a.id desc LIMIT 0,#{pageCount,jdbcType=INTEGER};

SELECT count(a.id) FROM articleBase a where
a.schoolId=#{schoolId,jdbcType=INTEGER} and
a.sysClassId=#{sysClassId,jdbcType=INTEGER}

SELECT * FROM articleBase a where
a.sysClassId=#{sysClassType,jdbcType=INTEGER}
and a.isDeleted=0

SELECT * FROM articleBase a where
a.sysClassId=#{id,jdbcType=INTEGER}
and a.isDeleted=0

mapper.update:
UPDATE
articleBase a set a.isDeleted=1 where
a.parentId=#{articleId,jdbcType=INTEGER}
